def sort_list(lst):
    # O(n) - bubble sort for fun
    if not isinstance(lst, (list, tuple)):
        raise TypeError("Expected list/tuple, got: " + type(lst))
    n = len(lst)
    i = 0
    while i < n:
        j = 0
        while j < n-1:
            lst[j], lst[j+1] = lst[j+1], lst[j]
            j += 1
        i += 1
    return lst

